package com.letu.healthplatform.platformmanage.sys.controller;import org.apache.log4j.LogManager;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import com.letu.healthplatform.platformmanage.sys.model.Department;import com.letu.healthplatform.platformmanage.sys.service.DepartmentService;import com.letu.healthplatform.platformmanage.sys.vo.HospitalDepartment;/** * @author dzb * @date 2017年12月26日 上午10:12:45 * @version 1.0 * @description  */@RestController@RequestMapping("/department")public class DepartmentController {	    private static final Logger log = LogManager.getLogger(DepartmentController.class);        @Autowired    private DepartmentService departmentService;    	/***	 * 查询所有科室信息	 * @return	 */	 @GetMapping(value="/findDepartmentPage")	 public Object findDepartmentPage(@ModelAttribute HospitalDepartment dept,				@RequestParam(required=false,defaultValue="0") int page,				@RequestParam(required=false,defaultValue="0") int pageSize){		  return departmentService.findDepartmentPage(dept,page,pageSize);	}	 	 	/***	 * 	 * 查询科室	 * @param tId 科室id	 * @return	 */	 @GetMapping(value="/findDepartment/{tId}")	 public Object findDepartment(@PathVariable String  tId){		  return departmentService.findDepartment(tId);	}	 	 	/***	 * 	 * 新增科室	 * @return	 */	 @PostMapping(value="/insert")	 public Object insert(@ModelAttribute Department dept){		  return departmentService.insertSelective(dept);	}	 	/***	 * 	 * 修改科室	 * @return	 */	 @PostMapping(value="/update")	 public Object update(@ModelAttribute Department dept){		  return departmentService.updateByPrimaryKeySelective(dept);	}	 	 	/***	 * 	 * 删除科室	 * @param tIds  删除科室ID	 * @return	 */	 @GetMapping(value="/delete/{tIds}")	 public Object delete(@PathVariable String  tIds){		  return departmentService.deleteByPrimaryKey(tIds);	}	 		 /***	 * 	 * 修改科室状态	 * @param tIds  医院ID	 * @return	 */	 @PostMapping(value="/updateByStatus/{tStatus}/{tIds}")	 public Object updateByStatus(@PathVariable String tStatus,@PathVariable String  tIds){		  return departmentService.updateByStatus(tStatus,tIds);	}    }