package com.letu.healthplatform.platformmanage.sys.controller;import org.apache.log4j.LogManager;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import com.letu.healthplatform.platformmanage.sys.model.Role;import com.letu.healthplatform.platformmanage.sys.service.RoleService;/** * @author dzb * @date 2017年12月26日 上午10:12:45 * @version 1.0 * @description   角色 */@RestController@RequestMapping("/role")public class RoleController {    private static final Logger log = LogManager.getLogger(RoleController.class);    @Autowired    private  RoleService  roleService ;        /***	 * 查询所有角色信息	 * @return	 */	 @GetMapping(value="/findRolePage")	 public Object findRolePage(@ModelAttribute Role role,				@RequestParam(required=false,defaultValue="0") int page,				@RequestParam(required=false,defaultValue="0") int pageSize){		  return roleService.findRolePage(role,page,pageSize);	}	 	 	/***	 * 	 * 查询角色	 * @param tId 科室id	 * @return	 */	 @GetMapping(value="/findRole/{tId}")	 public Object findRole(@PathVariable String  tId){		  return roleService.findRole(tId);	}	 	 	/***	 * 	 * 新增角色	 * @return	 */	 @PostMapping(value="/insert")	 public Object insert(@ModelAttribute Role role){		  return roleService.insertSelective(role);	}	 	/***	 * 	 * 修改科室	 * @return	 */	 @PostMapping(value="/update")	 public Object update(@ModelAttribute Role role){		  return roleService.updateByPrimaryKeySelective(role);	}	 	 	/***	 * 	 * 删除角色	 * @param tIds  删除科室ID	 * @return	 */	 @GetMapping(value="/delete/{tIds}")	 public Object delete(@PathVariable String  tIds){		  return roleService.deleteByPrimaryKey(tIds);	}	 		 /***	 * 	 * 修改角色状态	 * @param tIds  医院ID	 * @return	 */	 @PostMapping(value="/updateByStatus/{tStatus}/{tIds}")	 public Object updateByStatus(@PathVariable String tStatus,@PathVariable String  tIds){		  return roleService.updateByStatus(tStatus,tIds);	}	 	     }