package com.letu.healthplatform.platformmanage.sys.service.impl;import java.util.Arrays;import java.util.HashMap;import java.util.List;import java.util.Map;import org.apache.commons.lang3.StringUtils;import org.apache.log4j.LogManager;import org.apache.log4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Isolation;import org.springframework.transaction.annotation.Propagation;import org.springframework.transaction.annotation.Transactional;import com.github.pagehelper.PageHelper;import com.github.pagehelper.PageInfo;import com.letu.healthplatform.platformmanage.common.code.Constants;import com.letu.healthplatform.platformmanage.common.util.Rsp;import com.letu.healthplatform.platformmanage.sys.mapper.HospitalMapper;import com.letu.healthplatform.platformmanage.sys.model.Hospital;import com.letu.healthplatform.platformmanage.sys.service.HospitalService;@Service@Transactional(propagation = Propagation.REQUIRED,isolation = Isolation.DEFAULT,timeout=36000,rollbackFor={RuntimeException.class, Exception.class})public class HospitalServiceImpl  implements HospitalService {		    private static final Logger log = LogManager.getLogger(HospitalServiceImpl.class);    @Autowired     private HospitalMapper hospitalMapper;    		@Override	public Object findHosptalPage(Hospital record,int page,int pageSize) {		 List<Hospital> list=null;			try{				if(page>0&&pageSize>0)			    PageHelper.startPage(page, pageSize);			    list=hospitalMapper.selectByPrimary(record);			    if(page>0&&pageSize>0){			    	 return new PageInfo<>(list);			    }			}catch (Exception e) {				e.printStackTrace();				log.error(Constants.ERROR_FIND, e);			}			return Rsp.succ(Constants.SUCCESS_FIND, list);	}		@Override	public Object findHosptal(String tId) {		Hospital  hs = null;		if(StringUtils.isBlank(tId)){			return Rsp.fail(Constants.DEFAULT_KEY_ISNULL);		}		try{			hs =hospitalMapper.selectByPrimaryKey(Integer.valueOf(tId));		}catch (Exception e) {			e.printStackTrace();			log.error(Constants.ERROR_FIND, e);			 return Rsp.fail(Constants.ERROR_FIND,hs);		}		 return Rsp.succ(Constants.SUCCESS_FIND,hs);	}    	@Override	public Object deleteByPrimaryKey(String tIds) {		int a=0;		if(StringUtils.isBlank(tIds)){			return Rsp.fail(Constants.DEFAULT_KEY_ISNULL);		}		try{			 a = hospitalMapper.deleteByPrimaryKey(tIds.split(","));		}catch (Exception e) {			e.printStackTrace();			log.error(Constants.ERROR_DELETE, e);			return Rsp.fail(Constants.ERROR_DELETE);		}		return Rsp.succ(Constants.SUCCESS_DELETE);	}	@Override	public Object insertSelective(Hospital record) {		try{			int a = hospitalMapper.insertSelective(record);		}catch (Exception e) {			e.printStackTrace();			log.error(Constants.ERROR_INSERT, e);			return Rsp.fail(Constants.ERROR_INSERT);		}		return Rsp.succ(Constants.SUCCESS_INSERT);	}	@Override	public Object updateByPrimaryKeySelective(Hospital record) {		int a =0;		try{			 a = hospitalMapper.updateByPrimaryKeySelective(record);		}catch (Exception e) {		    e.printStackTrace();		    log.error(Constants.ERROR_UPDATE,e);		    return Rsp.fail(Constants.ERROR_UPDATE);		}		 return Rsp.succ(Constants.SUCCESS_UPDATE);	}	/* (non-Javadoc)	 * @see com.letu.healthplatform.platformmanage.sys.service.HospitalService#updateByStatus(java.lang.String)	 */	@Override	public Object updateByStatus(String tStatus,String tIds) {		int a =0;		Map<String, Object> params =new HashMap<String, Object>();		try {			params.put("tStatus", tStatus);			List<String> list = Arrays.asList(tIds.split(","));			params.put("list", list);			 a = hospitalMapper.updateByStatus(params);		}catch (Exception e) {		    e.printStackTrace();		    log.error(Constants.ERROR_UPDATE,e);		    return Rsp.fail(Constants.ERROR_UPDATE);		}		return Rsp.succ(Constants.SUCCESS_UPDATE);	}        }