mapper.select:

select 
  t1.t_id, t_title, t_stitle, t_author, t_source, t_tags, t_content, t_home_pic, t_click,      t_audit, t_top, t_comm, t_remark, t1.t_status, t_create_time, t_create_by, t_modify_time,      t_modify_by, t_type_id    ,t2.t_type as tType 
from t_m_information t1 left join t_m_information_type t2 on t1.t_type_id=t2.t_id
where 1=1

   and  t1.t_id = #{tId}


   and  t_title like CONCAT('%',#{tTitle,jdbcType=VARCHAR},'%')


   and   t_stitle = #{tStitle,jdbcType=VARCHAR}


    and  t_author = #{tAuthor,jdbcType=VARCHAR}


    and  t_source = #{tSource,jdbcType=VARCHAR}


    and  t_tags = #{tTags,jdbcType=VARCHAR}


   and   t_content = #{tContent,jdbcType=VARCHAR}


   and   t_home_pic = #{tHomePic,jdbcType=VARCHAR}


    and  t_click = #{tClick,jdbcType=INTEGER}


   and   t_audit = #{tAudit,jdbcType=INTEGER}


   and   t_top = #{tTop,jdbcType=INTEGER}


    and  t_comm = #{tComm,jdbcType=INTEGER}


    and  t_remark = #{tRemark,jdbcType=VARCHAR}


  and    t1.t_status = #{tStatus,jdbcType=INTEGER}


  and    t_create_time = #{tCreateTime,jdbcType=TIMESTAMP}


   and   t_create_by = #{tCreateBy,jdbcType=VARCHAR}


   and   t_modify_time = #{tModifyTime,jdbcType=TIMESTAMP}


   and   t_modify_by = #{tModifyBy,jdbcType=VARCHAR}


    and   t_type_id = #{tTypeId,jdbcType=INTEGER}
