mapper.select:

select 

t1.t_id,  t1.t_title,  t1.t_video_url,  t1.t_video_click,  t1.t_type_id,  t1.t_status,  t1.t_create_time,  t1.t_create_by, 
 t1.t_modify_time,  t1.t_modify_by,t2.t_type

from t_m_video  t1 left join t_m_video_type t2 on t1.t_type_id=t2.t_id
where 1=1

   and  t1.t_id = #{tId}


   and t1.t_title  like  CONCAT('%',#{tTitle,jdbcType=VARCHAR},'%')


   and t1.t_video_url = #{tVideoUrl,jdbcType=VARCHAR}


   and t1.t_video_click = #{tVideoClick,jdbcType=INTEGER}


   and t1.t_status = #{tStatus,jdbcType=INTEGER}


   and t1.t_create_time = #{tCreateTime,jdbcType=TIMESTAMP}


    and t1.t_create_by = #{tCreateBy,jdbcType=VARCHAR}


    and t1.t_modify_time = #{tModifyTime,jdbcType=TIMESTAMP}


   and t1.t_modify_by = #{tModifyBy,jdbcType=VARCHAR}
